mapper.select:
SELECT b.id, b.number, b.name, b.describe_msg AS describeMsg, b.attachment_id AS attachmentId, b.sort, b.title, b.keywords, b.description, b.alt, b.code, b.banner_id AS bannerId, b.click_url AS clickUrl, b.create_by AS createBy, b.create_time AS createTime, b.update_by AS updateBy, b.update_time AS updateTime, a.filename AS fileName, a.filesize AS fileSize, a.filetype AS fileType, a.filepath AS filePath, a.file_id AS fileId FROM module b LEFT JOIN system_attachment a ON b.attachment_id=a.id WHERE b.belong=#{belong}
and b.home_page_display=#{homePageDisplay}
ORDER BY b.sort
