mapper.select:
SELECT b.id, b.number, b.name, b.describe_msg AS describeMsg, b.module_id AS moduleId, b.attachment_id AS attachmentId, b.seo_image_id AS seoImageId, b.sort, b.title, b.keywords, b.description, b.alt, b.content, b.code, m.code AS moduleCode, b.click_url AS clickUrl, b.create_by AS createBy, b.create_time AS createTime, b.update_by AS updateBy, b.update_time AS updateTime FROM project b JOIN module m ON b.module_id=m.id AND m.belong=#{category} WHERE b.id !=#{projectId} AND ( 1=0
OR b.keywords LIKE "%${item}%"
) ORDER BY b.create_time desc LIMIT 6
